REPLACE INTO `SmsMessage` (`SmsMessageId`, `SpecialityGroupId`, `Type`, `Event`, `Language`, `Message`, `LastUpdated`) VALUES
(1, 1, 'GENERAL', 'REGISTRATION', 'English', 'MUHC - Cedars Cancer Centre: Registration for SMS messages is confirmed. To unsubscribe, please inform reception. Please check-in via SMS upon arrival to the hospital. More details at: https://tinyurl.com/y6qj53lx', '2022-01-22 07:34:26'),
(2, 1, 'GENERAL', 'REGISTRATION', 'French', 'CUSM - Centre du cancer des Cèdres: l\'inscription pour les notifications par SMS est confirmée. Pour vous désabonner, veuillez en informer la réception. Veuillez-vous enregistrer via SMS à votre arrivée à l\'hôpital. Plus de détails à: https://tinyurl.com/5n8dm6dv', '2023-04-19 17:40:14'),
(3, NULL, 'GENERAL', 'FAILED_CHECK_IN', 'English', 'MUHC: There is a problem checking in for your appointment(s). If you have an appointment today, please go to the reception to complete the check-in process.', '2020-08-02 17:22:42'),
(4, NULL, 'GENERAL', 'FAILED_CHECK_IN', 'French', 'CUSM: Il n\'a pas été possible de vous enregistrer pour votre rendez-vous. Si vous avez un rendez-vous aujourd\'hui, veuillez vous diriger à la réception pour vous enregistrer.', '2020-08-04 22:04:14'),
(5, NULL, 'GENERAL', 'UNKNOWN_COMMAND', 'English', 'MUHC: You have not been checked-in. To check-in for an appointment, please reply with the word "arrive". No other messages are accepted.', '2020-08-04 21:31:35'),
(8, NULL, 'GENERAL', 'UNKNOWN_COMMAND', 'French', 'CUSM: Vous n\'avez pas été enregistré(e). Pour vous enregister pour votre rendez-vous, svp repondez "arrive". Aucun autre message ne sera accepté.', '2020-08-04 21:56:43'),
(9, 1, 'GENERAL', 'CHECK_IN', 'English', 'MUHC - Cedars Cancer Centre: You have checked in for your appointment(s): <app>You do not need to check-in at the kiosk. You will receive an SMS when you are called to the waiting room. More details at: https://tinyurl.com/y6peoged', '2023-03-29 12:54:31'),
(10, 1, 'GENERAL', 'CHECK_IN', 'French', 'CUSM - Centre du cancer des Cèdres: Vous êtes enregistré pour vos rendez-vous:<app>Vous ne devez pas vous enregistrer à la borne. Vous recevrez un SMS quand vous serez appelé à la salle d’attente. Plus de détails à: https://tinyurl.com/y6a7h9p6', '2023-03-29 12:54:31'),
(11, 1, 'GENERAL', 'REMINDER', 'English', 'MUHC - Cedars Cancer Centre: Reminder for your appointment(s):<app>To check-in for an appointment, please respond to this message with *arrive* when you arrive at the hospital tomorrow. More details at: https://tinyurl.com/59x3eptk', '2023-04-19 17:36:08'),
(12, 1, 'GENERAL', 'REMINDER', 'French', 'CUSM - Centre du cancer des Cèdres: Rappel pour vos rendez-vous:<app>Pour vous enregistrer pour un rendez-vous, répondez à ce numéro avec *arrive* lorsque vous arrivez à l\'hôpital demain. Plus de détails à: https://tinyurl.com/y32n68mg', '2023-03-29 12:55:41'),
(13, 1, 'RADONC', 'REMINDER', 'English', 'MUHC - Cedars Cancer Centre: Reminder for your appointment(s):<app>To check-in for an appointment, please respond to this message with *arrive* when you arrive at the hospital tomorrow. More details at: https://tinyurl.com/2825bwkp', '2023-03-29 12:58:37'),
(14, 1, 'RADONC', 'REMINDER', 'French', 'CUSM - Centre du cancer des Cèdres: Rappel pour vos rendez-vous:<app>Pour vous enregistrer pour un rendez-vous, répondez à ce numéro avec *arrive* lorsque vous arriverez à l\'hôpital demain. Plus de détails à: https://tinyurl.com/yhcma5kb', '2023-03-29 12:58:37'),
(15, 1, 'TELEMED', 'REMINDER', 'English', 'MUHC - Cedars Cancer Centre: Reminder for your virtual appointment(s): <app>This is a virtual visit. DO NOT GO TO THE HOSPITAL. You will be contacted via phone by your healthcare provider within four hours of your appointment time. More details at: https://tinyurl.com/ybce35hq', '2022-07-20 18:10:52'),
(16, 1, 'TELEMED', 'REMINDER', 'French', 'CUSM - Centre du cancer des Cèdres: Rappel pour vos rendez-vous virtuels:<app>Il s\'agit d\'une consultation virtuelle. NE VOUS PRÉSENTEZ PAS À L\'HÔPITAL. Votre professionnel de la santé vous contactera par téléphone dans les quatre heures suivant l\'heure de votre visite virtuelle. Pour plus de détails: https://tinyurl.com/ycr7zp6p', '2022-07-20 18:10:25'),
(17, 1, 'RADONC', 'CHECK_IN', 'English', 'MUHC - Cedars Cancer Centre: You have checked in for your appointment(s): <app>You do not need to check-in at the kiosk. You will receive a message when you are called. More details at: https://tinyurl.com/yxjt65yk', '2023-03-29 12:57:04'),
(18, 1, 'RADONC', 'CHECK_IN', 'French', 'CUSM - Centre du cancer des Cèdres: Vous êtes enregistré pour vos rendez-vous:<app>Vous ne devez pas vous enregistrer à la borne. Vous recevrez un message quand vous serez appelé pour votre rendez-vous. Plus de détails à: https://tinyurl.com/y64434o3', '2023-03-29 12:57:04'),
(23, 2, 'GENERAL', 'REGISTRATION', 'English', 'MUHC: Registration for SMS notifications is confirmed. During your COVID-19 remote monitoring at home, the invitation link to the Zoom teleconsultation will be sent to you with this number if necessary. Don\'t forget to check your messages.', '2022-01-22 07:34:26'),
(24, 2, 'GENERAL', 'REGISTRATION', 'French', 'CUSM : L\'inscription pour les notifications par SMS est confirmée. Lors de votre suivi à distance COVID-19 à domicile, le lien d\'invitation à la téléconsultation Zoom vous sera envoyé avec ce numéro si nécessaire. N\'oubliez pas de vérifier vos messages.', '2022-01-22 07:34:26'),
(25, 1, 'ZOOM', 'REMINDER', 'English', 'MUHC - Cedars Cancer Centre: Reminder for your Zoom Video Conference appointment(s): \r\n<app>\r\nThis is a virtual consultation via Zoom video conference. DO NOT GO TO THE HOSPITAL. Please connect to Zoom from home at the time of your appointment. Please click on the following link for detailed instructions: https://tinyurl.com/yc8q5enx', '2022-01-22 07:34:26'),
(26, 1, 'ZOOM', 'REMINDER', 'French', 'CUSM - Centre du cancer des Cèdres: Rappel pour vos rendez-vous par visioconférence Zoom: \r\n<app>\r\nIl s\'agit d\'une consultation virtuelle par visioconférence Zoom. NE VOUS PRÉSENTEZ PAS À L\'HÔPITAL. Veuillez vous connecter à Zoom à partir de votre domicile lors de votre rendez-vous. Veuillez cliquer sur le lien suivant pour des instructions détaillées : https://tinyurl.com/y9je3re2', '2022-01-22 07:34:26');
